var access_token="239600873237336|UgqAGE9n6CUUQ1SWU0HE3wSGWTQ"

var listItem=`
                
    {{#posts.data}}
        <div class="card facebook-card">
          <div class="card-header no-border">
            <div class="facebook-avatar"><img src="{{picture.data.url}}" width="34" height="34"></div>
            <div class="facebook-name">{{name}}</div>
            {{!<div class="facebook-date">Monday at 3:47 PM</div>}}
          </div>
          <div class="card-content">
            <p>{{message}}</p>
            {{#attachments.data}}
                <a href="{{target.url}}_newPage">
                    <img src="{{media.image.src}}" width="100%">
                </a>
            {{/attachments.data}}
          </div>
        </div>
    {{/posts.data}}
                `;

function onCreate(){    
    $.ajax({
       url:"https://graph.facebook.com/"+config.pageId,
       dataType:"json",
       data:{
           access_token:access_token,
           fields:'name,picture,posts{attachments,message}'
       },
        success:function(resp){
            var op=Mustache.render(listItem,resp);
            $("#posts").html(op);
        },
        error:function(){
        
       }
    }) ;    
}

