var apiKey="AIzaSyDFRirEHjfwwr_uNaTfAtruPbWgAan0GCg"

var listItem=`
                <ul>
                    {{#items}}
                        <li>
                            <a href="https://www.youtube.com/embed/{{contentDetails.videoId}}_newPage">
                            <div class="item-content">
                                <div class="item-media"><img src="{{snippet.thumbnails.default.url}}"></div>
                                <div class="item-inner">
                                  <div class="item-title-row">
                                    <div class="item-title">{{snippet.title}}</div>
                                  </div>
                                  <div class="item-subtitle">{{snippet.channelTitle}}</div>
                                </div>
                            </div>
                            </a>
                        </li>
                    {{/items}}
                </ul>`;

function onCreate(){    
    $.ajax({
       url:"https://www.googleapis.com/youtube/v3/playlistItems",
       dataType:"json",
       data:{
            key:apiKey,
            part  : 'snippet,contentDetails', 
            maxResults : 50,
            playlistId:config.playlistId
       },
        success:function(resp){
            $("#playlist").html(JSON.stringify(resp));
            var op=Mustache.render(listItem,resp);
            $("#playlist").html(op);
        },
        error:function(){}
    }) ;    
}

